def get_summarization_calibration_text():
    """Generate formatted calibration text for summarization.

    Returns:
        str: Newline-separated summarization prompts
    """
    return "\n\n".join(SUMMARIZATION_CALIBRATION)


def save_summarization_calibration(filepath: str = "calibration_summarization.txt"):
    """Save summarization calibration to a file.

    Args:
        filepath: Path to save calibration file
    """
    with open(filepath, 'w') as f:
        f.write(get_summarization_calibration_text())
    print(f"Saved {len(SUMMARIZATION_CALIBRATION)} summarization calibration examples to {filepath}")


if __name__ == "__main__":
    save_summarization_calibration()
    print(f"\nTotal questions: {len(SUMMARIZATION_CALIBRATION)}")
    print(f"Categories:")
    print(f"  - Technical Articles: 10")
    print(f"  - Business & Finance: 10")
    print(f"  - Scientific & Academic: 10")
    print(f"\nAverage text length: ~1,200-2,500 words per example")
    print(f"Total calibration corpus: ~50,000 words")
